(s);					&lt;span class=&#34;hljs-comment&#34;&gt;//遍历 &lt;/span&gt;&lt;br&gt;	&lt;span class=&#34;hljs-keyword&#34;&gt;for&lt;/span&gt;(&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; i=&lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt;;i&amp;lt;=&lt;span class=&#34;hljs-number&#34;&gt;5&lt;/span&gt;;i++)&amp;#123;										&lt;span class=&#34;hljs-comment&#34;&gt;//为了对比GetTop和Pop操作 &lt;/span&gt;&lt;br&gt;		&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; x; &lt;br&gt;		&lt;span class=&#34;hljs-built_in&#34;&gt;GetTop&lt;/span&gt;(s,x);											&lt;span class=&#34;hljs-comment&#34;&gt;//取栈顶元素 &lt;/span&gt;&lt;br&gt;		cout&amp;lt;&amp;lt;&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;这是GetTop操作,栈顶元素为&amp;quot;&lt;/span&gt;&amp;lt;&amp;lt;x&amp;lt;&amp;lt;endl;&lt;br&gt;		cout&amp;lt;&amp;lt;&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;这是GetTop操作后的栈遍历:&amp;quot;&lt;/span&gt;;&lt;br&gt;		&lt;span class=&#34;hljs-built_in&#34;&gt;PrintSqStack&lt;/span&gt;(s); &lt;br&gt;		&lt;span class=&#34;hljs-built_in&#34;&gt;Pop&lt;/span&gt;(s,x);												&lt;span class=&#34;hljs-comment&#34;&gt;//出栈 &lt;/span&gt;&lt;br&gt;		cout&amp;lt;&amp;lt;&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;这是Pop操作,出栈的元素为&amp;quot;&lt;/span&gt;&amp;lt;&amp;lt;x&amp;lt;&amp;lt;endl;&lt;br&gt;		&lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt;(!&lt;span class=&#34;hljs-built_in&#34;&gt;StackEmpty&lt;/span&gt;(s))&lt;br&gt;		&amp;#123;&lt;br&gt;			cout&amp;lt;&amp;lt;&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;这是Pop操作后的栈遍历:&amp;quot;&lt;/span&gt;;&lt;br&gt;			&lt;span class=&#34;hljs-built_in&#34;&gt;PrintSqStack&lt;/span&gt;(s); &lt;br&gt;		&amp;#125;&lt;span class=&#34;hljs-keyword&#34;&gt;else&lt;/span&gt;&amp;#123;&lt;br&gt;			cout&amp;lt;&amp;lt;&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;此时栈空！&amp;quot;&lt;/span&gt;&amp;lt;&amp;lt;endl;&lt;br&gt;		&amp;#125;&lt;br&gt;		&lt;br&gt;	&amp;#125;&lt;br&gt;	&lt;span class=&#34;hljs-keyword&#34;&