=tmp-&amp;gt;next;&lt;br&gt;	&amp;#125;&lt;br&gt;	cout&amp;lt;&amp;lt;endl; &lt;br&gt;&amp;#125;	&lt;br&gt;&lt;span class=&#34;hljs-function&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;void&lt;/span&gt; &lt;span class=&#34;hljs-title&#34;&gt;Insert&lt;/span&gt;&lt;span class=&#34;hljs-params&#34;&gt;(LinkList &amp;amp;L,&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; i,&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; x)&lt;/span&gt;&lt;/span&gt;&amp;#123;&lt;br&gt;	LNode *p;&lt;br&gt;	LNode *s=(LNode*)&lt;span class=&#34;hljs-built_in&#34;&gt;malloc&lt;/span&gt;(&lt;span class=&#34;hljs-built_in&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;sizeof&lt;/span&gt;&lt;/span&gt;(LNode));				&lt;span class=&#34;hljs-comment&#34;&gt;//申请空间 &lt;/span&gt;&lt;br&gt;	s-&amp;gt;data=x;&lt;br&gt;	s-&amp;gt;next=&lt;span class=&#34;hljs-literal&#34;&gt;NULL&lt;/span&gt;; &lt;br&gt;	p=&lt;span class=&#34;hljs-built_in&#34;&gt;GetElem&lt;/span&gt;(L,i&lt;span class=&#34;hljs-number&#34;&gt;-1&lt;/span&gt;);&lt;br&gt;	&lt;span class=&#34;hljs-keyword&#34;&gt;if&lt;/span&gt;(p!=&lt;span class=&#34;hljs-literal&#34;&gt;NULL&lt;/span&gt;)&amp;#123;&lt;br&gt;		s-&amp;gt;next =p-&amp;gt;next;&lt;br&gt;		p-&amp;gt;next=s;&lt;br&gt;	&amp;#125;&lt;span class=&#34;hljs-keyword&#34;&gt;else&lt;/span&gt;&amp;#123;&lt;br&gt;		cout&amp;lt;&amp;lt;&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;插入值非法&amp;quot;&lt;/span&gt;&amp;lt;&amp;lt;endl;&lt;br&gt;		&lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; ; &lt;br&gt;	&amp;#125;&lt;br&gt;	&lt;br&gt;&amp;#125;&lt;br&gt;&lt;span class=&#34;hljs-comment&#34;&gt;//注意与插入结点相对比!!!!!!!!&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;hljs-function&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;void&lt;/span&gt; &lt;span class=&#34;hljs-title&#34;&gt;Delect&lt;/span&gt;&lt;span class=&#34;hljs-params&#34;&gt;(LinkList &amp;amp;L,&lt;span class=&#34;hljs-keyword&#34;&gt;int&lt;/span&gt; i)&lt;/span&gt;&lt;/span&gt;&amp;#123